mapper.select:

select donkeytimeId,donkeyId, RFIDInfo, size, time from donkeyhistory t

	1=1

		and t.donkeyId = #{donkeyId}


		and t.RFIDInfo=#{RFIDInfo}


		and t.size = #{size}


order by donkeytimeId desc limit #{pageNum},#{limit}

select count(*) from donkeyhistory t

	1=1

		and t.donkeyId = #{donkeyId}


		and t.RFIDInfo=#{RFIDInfo}


		and t.size = #{size}
